import pop from '../core/pop.js';
import push from '../core/push.js';
import merge from '../core/merge.js';
import siftup from '../core/siftup.js';
import siftdown from '../core/siftdown.js';
import remove from '../core/remove.js';

export default function DAryHeap(arity, compare) {
	// Arity of this heap

	this.arity = arity;

	// The comparison function

	this.compare = function (a, b) {
		return compare(a.value, b.value);
	};

	// The original comparison function

	this._compare = compare;

	// Array used to store values

	this.array = [];

	// Size of the heap

	this.length = 0;
}

DAryHeap.Reference = function (index, value) {
	this.index = index;
	this.value = value;
};

DAryHeap.prototype.swap = function (a, i, j) {
	const tmp = a[i];
	a[i] = a[j];
	a[j] = tmp;

	a[i].index = i;
	a[j].index = j;
};

DAryHeap.prototype.head = function () {
	if (this.length === 0) {
		return undefined;
	}

	return this.array[0].value;
};

DAryHeap.prototype.headreference = function () {
	if (this.length === 0) {
		return null;
	}

	return this.array[0];
};

DAryHeap.prototype.pop = function () {
	if (this.length === 0) {
		return undefined;
	}

	const a = this.array;
	const i = 0;
	const j = a.length;

	const reference = pop(this.arity, this.compare, this.swap, a, i, j);

	a.pop();

	--this.length;

	return reference.value;
};

DAryHeap.prototype.popreference = function () {
	if (this.length === 0) {
		return null;
	}

	const a = this.array;
	const i = 0;
	const j = a.length;

	const reference = pop(this.arity, this.compare, this.swap, a, i, j);

	a.pop();

	--this.length;

	return reference;
};

DAryHeap.prototype.push = function (value) {
	const a = this.array;
	const i = 0;
	const j = a.length;

	const reference = new DAryHeap.Reference(j, value);

	a.push(reference);

	push(this.arity, this.compare, this.swap, a, i, j);

	++this.length;

	return reference;
};

DAryHeap.prototype.pushreference = function (reference) {
	const a = this.array;
	const i = 0;
	const j = a.length;

	reference.index = j;
	a.push(reference);

	push(this.arity, this.compare, this.swap, a, i, j);

	++this.length;
};

DAryHeap.prototype.merge = function (other) {
	const a = this.array;
	const i = 0;
	const j = a.length;

	// Concat arrays of both heaps

	const b = a.concat(other.array);
	this.array = b;

	const k = b.length;

	// Update index of concatenated elements

	for (let t = j; t < k; ++t) {
		b[t].index = t;
	}

	merge(this.arity, this.compare, this.swap, b, i, j, k);

	this.length += other.length;
};

DAryHeap.prototype.update = function (reference, value) {
	const d = this._compare(value, reference.value);

	if (d < 0) {
		this.decreasekey(reference, value);
	} else if (d > 0) {
		this.increasekey(reference, value);
	} else {
		// D === 0 does not imply reference.value === value

		reference.value = value;
	}
};

DAryHeap.prototype.decreasekey = function (reference, value) {
	const a = this.array;
	const i = 0;
	const j = a.length;
	const k = reference.index;

	reference.value = value;

	siftup(this.arity, this.compare, this.swap, a, i, j, k);
};

DAryHeap.prototype.increasekey = function (reference, value) {
	const a = this.array;
	const i = 0;
	const j = a.length;
	const k = reference.index;

	reference.value = value;

	siftdown(this.arity, this.compare, this.swap, a, i, j, k);
};

DAryHeap.prototype.delete = function (reference) {
	const a = this.array;
	const i = 0;
	const j = a.length;
	const k = reference.index;

	remove(this.arity, this.compare, this.swap, a, i, j, k);

	a.pop();

	--this.length;
};

import pop from '../core/pop.js';
import push from '../core/push.js';
import merge from '../core/merge.js';

export default function DAryHeapWithoutReferences(arity, compare) {
	// Arity of this heap

	this.arity = arity;

	// The comparison function

	this.compare = compare;

	// Array used to store values

	this.array = [];

	// Size of the heap

	this.length = 0;
}

DAryHeapWithoutReferences.prototype.swap = function (a, i, j) {
	const tmp = a[i];
	a[i] = a[j];
	a[j] = tmp;
};

DAryHeapWithoutReferences.prototype.head = function () {
	if (this.length === 0) {
		return undefined;
	}

	return this.array[0];
};

DAryHeapWithoutReferences.prototype.pop = function () {
	if (this.length === 0) {
		return undefined;
	}

	const a = this.array;
	const i = 0;
	const j = a.length;

	const value = pop(this.arity, this.compare, this.swap, a, i, j);

	a.pop();

	--this.length;

	return value;
};

DAryHeapWithoutReferences.prototype.push = function (value) {
	const a = this.array;
	const i = 0;
	const j = a.length;

	a.push(value);

	push(this.arity, this.compare, this.swap, a, i, j);

	++this.length;
};

DAryHeapWithoutReferences.prototype.merge = function (other) {
	const a = this.array;
	const i = 0;
	const j = a.length;

	const b = a.concat(other.array);
	this.array = b;

	const k = b.length;

	merge(this.arity, this.compare, this.swap, b, i, j, k);

	this.length += other.length;
};

import siftdown from './siftdown.js';

/**
 * Builds a heap in O(n) operations.
 *
 * @param {int} arity arity of the heap
 * @param {function} compare the comparison function
 * @param {function} swap the swap function
 * @param {array} a the array where the heap is stored
 * @param {int} i left inner bound
 * @param {int} j right outer bound
 *
 */

export default function makeheap(arity, compare, swap, a, i, j) {
	for (let k = (i + (j - i + arity - 2) / arity) | 0; k-- > i; ) {
		siftdown(arity, compare, swap, a, i, j, k);
	}
}

import siftup from './siftup.js';

/**
 * Merges heaps at intervals [i, j[ and [j, k[ in array *a*
 * into a new heap at interval [i, k[.
 *
 * Hypothesis :
 *
 *   - i <= j <= k
 *   - j - 1 is the last leaf ot the first heap
 *
 * @param {int} arity arity of the heap
 * @param {function} compare the comparison function
 * @param {function} swap the swap function
 * @param {array} a the array where the two heaps are stored
 * @param {int} i is the root of the first heap
 * @param {int} j is the root of the second heap
 * @param {int} k - 1 is the index of the last leaf in the second heap
 */

export default function merge(arity, compare, swap, a, i, j, k) {
	for (; j < k; ++j) {
		siftup(arity, compare, swap, a, i, j + 1, j);
	}
}

/**
 * Computes which child is the smallest according
 * to a comparison function.
 *
 * Hypothesis : i < j i.e. there should be at least one child
 *
 * @param {int} arity arity of the heap
 * @param {function} compare the comparison function
 * @param {function} swap the swap function
 * @param {array} a the array where the heap is stored
 * @param {int} i is the first child
 * @param {int} j - 1 is the last leaf
 */

export default function nextchild(arity, compare, swap, a, i, j) {
	const k = i + Math.min(arity, j - i);

	let best = i;

	for (++i; i < k; ++i) {
		if (compare(a[i], a[best]) < 0) {
			best = i;
		}
	}

	return best;
}

import siftdown from './siftdown.js';

/**
 * Pops the root from a d-ary heap.
 *
 * Hypothesis : i < j
 *
 * @param {int} arity arity of the heap
 * @param {function} compare the comparison function
 * @param {function} swap the swap function
 * @param {array} a the array where the heap is stored
 * @param {int} i is the root
 * @param {int} j - 1 is the last leaf
 */

export default function pop(arity, compare, swap, a, i, j) {
	// Decrement size of heap

	--j;

	// Put last leaf at root

	const popped = a[i];
	a[i] = a[j];

	// Sift down the new root

	siftdown(arity, compare, swap, a, i, j, i);

	// Return old root

	return popped;
}

/**
 * Sifts a node up to the root as if its priority was the highest.
 *
 * @param {int} arity arity of the heap
 * @param {function} compare the comparison function
 * @param {function} swap the swap function
 * @param {array} a the array where the heap is stored
 * @param {int} i is the root element
 * @param {int} j - 1 is the last leaf
 * @param {int} k is the target node
 */

export default function pull(arity, compare, swap, a, i, j, k) {
	let current = k - i;

	// While we are not the root

	while (current !== 0) {
		// Address of the parent in a zero-based
		// d-ary heap

		const parent = i + Math.floor((current - 1) / arity);

		// Swap with parent

		swap(a, i + current, parent);

		current = parent - i;
	}
}

import siftup from './siftup.js';

/**
 * Inserts the jth element of an array in an existing
 * dary heap in interval [i, j[.
 *
 * Hypothesis : i <= j
 *
 * @param {int} arity arity of the heap
 * @param {function} compare the comparison function
 * @param {function} swap the swap function
 * @param {array} a the array where the heap is stored
 * @param {int} i is the root
 * @param {int} j - 1 is the new leaf
 */

export default function push(arity, compare, swap, a, i, j) {
	// Sift up the new leaf

	return siftup(arity, compare, swap, a, i, j + 1, j);
}

import pull from './pull.js';
import pop from './pop.js';

/**
 * Removes a target element from a d-ary heap.
 *
 * Hypothesis : i < j
 *
 * @param {int} arity arity of the heap
 * @param {function} compare the comparison function
 * @param {function} swap the swap function
 * @param {array} a the array where the heap is stored
 * @param {int} i is the root
 * @param {int} j - 1 is the last leaf
 * @param {int} k is the target node
 */

export default function remove(arity, compare, swap, a, i, j, k) {
	// Pull target node all the way up

	pull(arity, compare, swap, a, i, j, k);

	// Pop target node

	return pop(arity, compare, swap, a, i, j);
}

import nextchild from './nextchild.js';

/**
 * Sifts down a node.
 *
 * @param {int} arity arity of the heap
 * @param {function} compare the comparison function
 * @param {function} swap the swap function
 * @param {array} a the array where the heap is stored
 * @param {int} i is the root element
 * @param {int} j - 1 is the last leaf
 * @param {int} k is the target node
 */

export default function siftdown(arity, compare, swap, a, i, j, k) {
	let current = k - i;

	while (true) {
		// Address of the first child in a zero-based
		// d-ary heap

		const firstchild = arity * current + 1;

		// If current node has no children
		// then we are done

		if (firstchild >= j - i) break;

		// If current value is smaller than its smallest
		// child then we are done

		const candidate = nextchild(arity, compare, swap, a, i + firstchild, j);

		if (compare(a[i + current], a[candidate]) <= 0) break;

		// Otherwise
		// swap with smallest child

		swap(a, i + current, candidate);

		current = candidate - i;
	}

	return i + current;
}

/**
 * Sifts up a node.
 *
 * @param {int} arity arity of the heap
 * @param {function} compare the comparison function
 * @param {function} swap the swap function
 * @param {array} a the array where the heap is stored
 * @param {int} i is the root element
 * @param {int} j - 1 is the last leaf
 * @param {int} k is the target node
 */

export default function siftup(arity, compare, swap, a, i, j, k) {
	let current = k - i;

	// While we are not the root

	while (current !== 0) {
		// Address of the parent in a zero-based
		// d-ary heap

		const parent = i + Math.floor((current - 1) / arity);

		// If current value is greater than its parent
		// then we are done

		if (compare(a[i + current], a[parent]) >= 0) break;

		// Otherwise
		// swap with parent

		swap(a, i + current, parent);

		current = parent - i;
	}

	return i + current;
}

import makeheap from './makeheap.js';
import pop from './pop.js';

export default function* sorted(arity, compare, swap, a, i, j) {
	makeheap(arity, compare, swap, a, i, j);

	for (; i < j; --j) yield pop(arity, compare, swap, a, i, j);
}

